])
const jy = ref('jy')
const updateMouseVars = (event, type) => {
  const el = event.currentTarget
  const rect = el.getBoundingClientRect()
  const x = event.clientX - rect.left
  const y = event.clientY - rect.top
  el.style.setProperty('--mouse-x', `${x}px`)
  el.style.setProperty('--mouse-y', `${y}px`)
  if (!type) {
    jy.value = jy.value
  } else {
    jy.value = type
  }
}
const list = ref([])
const activeNames = ref([])
const activeContent = ref()
const collapseChange = async (name) => {
  if (name) {
    const res = await getHelpcenterDetail(name)
    if (res.code === 200) {
      activeContent.value = res.data.content
    }
  }
}
const listResult = ref([])
const toRafSort = () => {
  listResult.value = tradeStore.secondContractCoinList.filter((it, inx) => {
    return it.coinType == 2
  })
}
onMounted(async () => {
  toRafSort()
  const aosInitElements = document.querySelectorAll('.aos-init')
  let lastScrollY = window.scrollY
  const observer = new IntersectionObserver(
    (entries) => {
      const currentScrollY = window.scrollY
      const isScrollingDown = currentScrollY > lastScrollY
      lastScrollY = currentScrollY
      entries.forEach((entry) => {
        const el = entry.target
        if (entry.isIntersecting && isScrollingDown) {
          el.classList.add('aos-animate')
        }

        // 如果不再可见，并且是向上滚动，移除类
        if (!entry.isIntersecting && !isScrollingDown) {
          el.classList.remove('aos-animate')
        }
      })
    },
    {
      threshold: 0.1
    }
  )
  aosInitElements.forEach((el) => {
    observer.observe(el)
  })
  const res = await getHelpcenter()
  let arr = []
  if (res.code === 200) {
    res.data.forEach((item) => {
      item.infoList.forEach((item) => {
        arr.push({
          ...item
        })
      })
    })
    list.value = arr.sort((a, b) => new Date(b.updateTime) - new Date(a.updateTime))
  }
})
